def rand_from_primegroup(q):
    number = random.randint(1,q-1)
    return number

def decipher(ciphertext,sk):
        ct_1 = ciphertext[0]
        ct_2 = ciphertext[1]
        ct_1_inverse = gy.invert(ct_1,N**2)
        
        m = ct_2 * gy.powmod(ct_1_inverse,sk,N**2) % (N**2)
        
        plaintext = (((m - 1) % N**2) // N) % N**2
        return plaintext

def FE_Encrypt(plaintext,pk):
        
        r = random.randint(1,N //4 -1)
        ct_1 = gy.powmod(g,r,N**2)
        ct_2 = gy.powmod((1 + N),plaintext, N ** 2) * gy.powmod(pk, r, N ** 2) % (N ** 2)
        return [ct_1,ct_2]

def FE_Decrypt(K,ct_m_2,y,skm):
        tmp_cm = 1
        for k in range(K):
              tmp_cm = tmp_cm * gy.powmod(ct_m_2[k],y[k],N**2) % N**2
        C_m = tmp_cm * skm % N**2
        plaintext = (((C_m - 1) % N**2) // N) % N**2
        return plaintext
        

